import React, {useState, useEffect} from "react";
import {
    Container,
    Row,
    Column
} from "../index";
import CircularProgress from "@material-ui/core/CircularProgress";
import makeStyles from "@material-ui/core/styles/makeStyles";
import NewsStyles from "./NewsStyles";

const useStyles = makeStyles(NewsStyles);
//5454cb1b9be0477fb63a1b9fc15db6eb - API key

const News = (props) => {
    const classes = useStyles();


    const [news, newsUpdate] = useState([]);

    useEffect(() => {
        let url = 'https://newsapi.org/v2/top-headlines?' +
            'country=us&' +
            'apiKey=5454cb1b9be0477fb63a1b9fc15db6eb';
        fetch(url)
            .then(response => {
                return response.json();
            })
            .then((data) => {
                newsUpdate(data.articles)
            });
    }, []);

    return (
        <Container>
            <Row>
                <Column>
                    {news ?
                        news.map((items, i) => (
                            <div key={i}>
                                {console.log(items)}
                               <h2>{items.author}</h2>
                                <p>{items.description}</p><br/>
                                <div className={classes.imagesWrapper}>
                                    <img src={items.urlToImage} alt=""/>
                                </div>
                                {items.title}
                            </div>
                        ))
                        : (<CircularProgress color="secondary" />)}
                </Column>
            </Row>
        </Container>
    )
};

export default News;
