const DOM = require('./index.js'),
  assert = require('assert');

describe('dom', () => {
  describe('elements', () => {
    const keys = Object.keys(DOM);

    it('has 132 elements', () => {
      assert(keys.length == 132)
    });

    it('each element is a factory for its type', () => {
      for (var i = 0; i < keys.length; i++) {
        const type = keys[i],
          factory = DOM[type];
        assert(typeof factory === 'function');
        assert(factory().type === type);
      }
    });

  });
});
